        payload = {
            "pair": token_pair,
            "amount": amount,
            "side": side
        }
        response = requests.post(f"{self.api_url}/v1/order", json=payload)
        if response.status_code == 200:
            return response.json()
        else:
            raise Exception(f"Failed to place order: {response.text}")

